configuration.styles:

    /* make header compact and responsive */
    .brand-logo { height: 46px; margin-right: 12px; vertical-align: middle; }
    .report-name { font-size: 1.1rem; font-weight: 600; }
    .topbar { display:flex; align-items:center; gap:12px; flex-wrap:wrap; }
    /* responsive table: allow horizontal scroll on small screens */
    .test-list table { width:100%; overflow-x:auto; display:block; }
    /* highlight failed tests rows slightly */
    .test-list tr.status-fail { box-shadow: inset 0 0 0 3px rgba(255,0,0,0.03); }
    /* small UI improvements */
    #searchFilter { padding:6px 10px; border-radius:4px; border:1px solid #ccc; width:220px; }

configuration.scripts:
            
                (function () {
                    document.addEventListener('DOMContentLoaded', function () {
                        // create a simple search box in the topbar
                        var nav = document.querySelector('.topbar') || document.querySelector('.nav');
                        if (!nav) return;

                        var input = document.createElement('input');
                        input.id = 'searchFilter';
                        input.placeholder = 'Filter tests (by name or status)...';
                        nav.insertBefore(input, nav.firstChild);

                        input.addEventListener('input', function (e) {
                            var q = e.target.value.toLowerCase().trim();
                            var rows = document.querySelectorAll('.test-list .test-row, .test-list tbody tr');
                            if (!rows) return;
                            rows.forEach(function (r) {
                                var txt = (r.innerText || '').toLowerCase();
                                r.style.display = q === '' || txt.indexOf(q) !== -1 ? '' : 'none';
                            });
                        });
                    });
                })();
